// ScoreContainer.jsx
import React, { useState } from 'react';

const ScoreContainer = () => {
  const [balls, setBalls] = useState(1);
  const [holeNumber, setHoleNumber] = useState(1);
  const [holeScore, setHoleScore] = useState(0);
  const [loopNumber, setLoopNumber] = useState(1);
  const [loopScore, setLoopScore] = useState(0);

  return (
    <div className="score-container">
      <div id="balls-display-container">
        <div id="balls-display">
          <input
            type="number"
            id="balls-input"
            value={balls}
            min="1"
            style={{ width: '50px' }}
            onChange={(e) => setBalls(e.target.value)}
          />
        </div>
        <div className="arrow"></div>
        <div id="ball-on-bar">
          <div>BALL</div>
          <div>ON</div>
          <div>BAR</div>
        </div>
      </div>
      <div id="time-display">
        <div className="time-label">TIME</div>
        <div id="time-value">00:00:00</div>
      </div>
      <div id="hole-display-container">
        <div id="hole-number-display">
          <div className="hole-label">HOLE</div>
          <input
            type="number"
            id="hole-number-input"
            value={holeNumber}
            min="1"
            max="10"
            style={{ width: '50px' }}
            onChange={(e) => setHoleNumber(e.target.value)}
          />
        </div>
        <div id="hole-score-display">
          <div className="hole-label">HOLE SCORE</div>
          <input
            type="number"
            id="hole-score-input"
            value={holeScore}
            style={{ width: '75px' }}
            onChange={(e) => setHoleScore(e.target.value)}
          />
        </div>
      </div>
      <div id="hole-display-container">
        <div id="hole-number-display">
          <div className="hole-label">LOOP</div>
          <input
            type="number"
            id="loop-number-input"
            value={loopNumber}
            min="1"
            max="10"
            style={{ width: '50px' }}
            onChange={(e) => setLoopNumber(e.target.value)}
          />
        </div>
        <div id="hole-score-display">
          <div className="hole-label">LOOP SCORE</div>
          <input
            type="number"
            id="loop-score-input"
            value={loopScore}
            style={{ width: '75px' }}
            onChange={(e) => setLoopScore(e.target.value)}
          />
        </div>
      </div>
      <div id="score-display">
        <div className="score-label">SCORE</div>
        <div id="score-value"></div>
      </div>
    </div>
  );
};

export default ScoreContainer;
