import React, { useState, useRef } from 'react';
import { createNewGameSession, resetTimeDisplay, resetScoreDisplay, startStopwatch, stopTimeDisplay, resetLoopDisplay } from '../utils/api';
import './styles/ButtonContainer.css';

const ButtonContainer = () => {
    const [progress, setProgress] = useState(0);
    const timerRef = useRef(null);

    const handleStartGame = () => {
        stopTimeDisplay();
        resetTimeDisplay();
        resetScoreDisplay();
        resetLoopDisplay();
        createNewGameSession();
        startStopwatch();
    };

    const handleMouseDown = () => {
        timerRef.current = setInterval(() => {
            setProgress(prev => {
                if (prev >= 100) {
                    clearInterval(timerRef.current);
                    handleStartGame();
                    return 100;
                }
                return prev + 1;
            });
        }, 30); // Adjust the interval as needed
    };

    const handleMouseUp = () => {
        clearInterval(timerRef.current);
        setProgress(0);
    };

    return (
        <div className="button-container">
            <button
                id="start-game-btn"
                onMouseDown={handleMouseDown}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseUp}
                style={{ background: `linear-gradient(135deg, #de9f2d ${progress}%, #f9e443 ${progress}%)` }}
            >
                Start Game
            </button>
        </div>
    );
};

export default ButtonContainer;
