import React from 'react';
import { createNewGameSession, resetTimeDisplay, resetScoreDisplay, startStopwatch, stopTimeDisplay, resetLoopDisplay } from '../utils/api';

const ButtonContainer = () => {
    const handleStartGame = () => {
        stopTimeDisplay();
        resetTimeDisplay();
        resetScoreDisplay();
        createNewGameSession();
        startStopwatch();
    };

    const handleEndGame = () => {
        stopTimeDisplay();
        resetTimeDisplay();
        resetScoreDisplay();
        resetLoopDisplay();
    };

    const handleResetGame = () => {
        stopTimeDisplay();
        resetTimeDisplay();
        resetScoreDisplay();
        createNewGameSession();
        startStopwatch();
    };

    return (
        <div className="button-container">
            <div className="timer-options">
                <div id="timer-options" className="options-label">Game Options</div>
                <div className="button-row">
                    <button id="start-game-btn" onClick={handleStartGame}>Start Game</button>
                    <button id="end-game-btn" onClick={handleEndGame}>End Game</button>
                    <button id="reset-game-btn" onClick={handleResetGame}>Reset Game</button>
                </div>
            </div>
        </div>
    );
};

export default ButtonContainer;
