import React, { useEffect, useState } from "react";
import "./styles/ProfileContainer.css";
import wizardImage from "../assets/images/wizard.png";
import lowryParcade from "../assets/images/lowry_parcade.png";

import { getPlayerData } from "../utils/api";
import StatCard from "./StatCard";
import SocialMediaHandles from "./SocialMediaHandles"; // Assuming this is the component we created earlier

const playerId = 1;

const ProfileContainer = () => {
  const [player, setPlayer] = useState({
    username: "",
    location: "",
    team: "",
    highestScore: "",
    homeArcade: "",
    twitch: "",
    youtube: "",
    instagram: "",
  });

  useEffect(() => {
    getPlayerData(playerId)
      .then((data) => {
        setPlayer({
          username: data.username,
          location: `${data.city_name}, ${data.state_name}`,
          team: data.team_name,
          highestScore: data.highest_score,
          homeArcade: data.home_arcade,
          twitch: data.twitch || "",
          youtube: data.youtube || "",
          instagram: data.instagram || "",
        });
      })
      .catch((error) => {
        console.error("Error fetching player data:", error);
      });
  }, []);

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-picture-container">
        <img src={wizardImage} alt="Profile" className="profile-picture" />
        </div>
        <div className="profile-details-container">
        <span id="profile-name">
            <h3>{player.username}</h3>
            <hr />

            </span>
          <div className="info-section">



              <span id="location">{player.location}</span>

            <div className="social-media-handles">
              {player.twitch && (
                <SocialMediaHandles data={{ twitch: player.twitch }} />
              )}
              {player.youtube && (
                <SocialMediaHandles data={{ youtube: player.youtube }} />
              )}
              {player.instagram && (
                <SocialMediaHandles data={{ instagram: player.instagram }} />
              )}
            </div>
          </div>
        </div>
      </div>



      <div className="stats-section">
        <h3>Stats</h3>
        <div className="stats-grid">
          <StatCard title="Number of Games" value="Too Many" />
          <StatCard title="Loops Completed" value="Alot" />
          <StatCard title="Highest Loop Score" value="5,370" />
          <StatCard title="Average Loop Score" value="*shrug*" />
          <StatCard title="Highest Total Score" value="2,168,560" />
          <StatCard title="Average Total Score" value="??" />
        </div>
      </div>
    </div>
  );
};

export default ProfileContainer;
