import React from 'react';
import Button from '../components/Button';

function ButtonContainer({ onScoreUpdate, onTimeUpdate }) {
  const startGame = () => {
    // Add your game start logic here
    console.log('Game started!');
    onScoreUpdate(0);  // Reset score on game start
    onTimeUpdate(0);   // Reset timer
  };

  const stopGame = () => {
    console.log('Game stopped!');
    // Logic to stop game
  };

  const resetGame = () => {
    console.log('Game reset!');
    onScoreUpdate(0);
    onTimeUpdate(0);
    // Logic to reset game
  };

  return (
    <div className="button-container">
      <Button label="Start Game" onClick={startGame} />
      <Button label="Stop Game" onClick={stopGame} />
      <Button label="Reset Game" onClick={resetGame} />
    </div>
  );
}

export default ButtonContainer;
