import React, { useEffect, useState } from 'react';
import './styles/ProfileContainer.css';
import wizardImage from '../assets/images/wizard.jpg';
import icbImage from '../assets/images/icb.jpg';

import { getPlayerData } from '../utils/api';
import StatCard from './StatCard';

const playerId = 1;


const ProfileContainer = () => {
    const [player, setPlayer] = useState({
        username: '',
        location: '',
        team: '',
        highestScore: '',
        homeArcade: '',
        socialMediaLinks: '',
        streamLinks: ''
    });

    useEffect(() => {
        getPlayerData(playerId).then(data => {
            setPlayer({
                username: data.username,
                location: `${data.city_name}, ${data.state_name}`,
                team: data.team_name,
                highestScore: data.highest_score,
                homeArcade: data.home_arcade,
                socialMediaLinks: data.socialMediaLinks,
            });
        }).catch(error => {
            console.error('Error fetching player data:', error);
        });
    }, []);

    return (
        <div className="profile-container">
            <div className="profile-header">
                <img src={wizardImage} alt="Profile" className="profile-picture" />
                <div className="profile-details">

                    <div className="info-section">
                        <h3>Info</h3>                    
                        <p><strong>Name:</strong> <span id="player-name" className="profile-name">{player.username}</span></p>
                        <p><strong>Location:</strong> <span id="location">{player.location}</span></p>
                        <p><strong>Team:</strong> <span id="team">{player.team}</span></p>
                        <p><strong>Highest Score:</strong> <span id="highest-score">{player.highestScore}</span></p>
                        <p><strong>Home Arcade:</strong> <span id="home-arcade">{player.homeArcade}</span></p>
                    </div>
                </div>
            </div>
            <div className="stats-section">
                <h3>Stats</h3>
                <div className="stats-grid">
                    <StatCard title="Number of Games" value="10" />
                    <StatCard title="Loops Completed" value="5" />
                    <StatCard title="Highest Loop Score" value="200" />
                    <StatCard title="Average Loop Score" value="150" />
                    <StatCard title="Highest Total Score" value="1000" />
                    <StatCard title="Average Total Score" value="800" />
                </div>
            </div>
            <div className="socials-section">
                <h3>Socials</h3>
                <p>Details about the player's social media links can be displayed here.</p>
            </div>
        </div>
    );
};

export default ProfileContainer;
